CREATE DEFINER=`` PROCEDURE `usp_A_AddEditConfig` (IN `_CrashEmail` VARCHAR(70), IN `_SupportEmail` VARCHAR(70), IN `_TimeZone` VARCHAR(50), IN `_AppVersionAndroid` DOUBLE, IN `_AppVersionIOS` DOUBLE, IN `_UserType` ENUM('Admin Web','Admin Android','Admin IOS','Employee Web','Employee Android','Employee IOS','Company Web','Company Android','Company IOS','Mentor Web','Mentor Android','Mentor IOS'), IN `_IPAddress` VARCHAR(16), IN `_CreatedBy` INT, IN `_ConfigID` INT, IN `_ModifiedBy` INT)  NO SQL BEGIN
  IF(_CrashEmail    ='')   THEN SET _CrashEmail =NULL; END IF;
  IF(_SupportEmail    ='') THEN SET _SupportEmail =NULL; END IF;
  IF(_TimeZone    ='') THEN SET _TimeZone =NULL; END IF;
  IF(_AppVersionAndroid    ='') THEN SET _AppVersionAndroid =NULL; END IF;
  IF(_AppVersionIOS    ='') THEN SET _AppVersionIOS =NULL; END IF;
  IF(_ModifiedBy = 0)     THEN SET _ModifiedBy =NULL; END IF;
  IF(_CreatedBy = '0')     THEN SET _CreatedBy  =NULL; END IF;
  SET @flag = 0;
  SET @TimeZone = (SELECT TimeZone  FROM sssm_config Limit 1);
  IF(IFNULL(@TimeZone,'') = '') THEN SET @TimeZone = "+00:00"; END IF;
  IF(_ConfigID > 0) THEN 
    SET @flag = (SELECT count(ConfigID) from sssm_config WHERE  ConfigID = _ConfigID);
  END IF;
  IF(_ConfigID = 0 AND @flag = 0) THEN
      INSERT INTO sssm_config(
          CrashEmail,
          SupportEmail,
          TimeZone,
          AppVersionAndroid,
          AppVersionIOS,
          CreatedBy,
          CreatedDate
        ) 
        VALUES(                             
              IFNULL(_CrashEmail,''),
              IFNULL(_SupportEmail,''),
              IFNULL(_TimeZone,''),
              IFNULL(_AppVersionAndroid,0),
              IFNULL(_AppVersionIOS,0),
              IFNULL(_CreatedBy,0),
              CONVERT_TZ(NOW(),@@session.time_zone,"+00:00")
              );
  SET @ID=(SELECT Fn_A_AddActivityLog('AddConfig',' has added new config.',_UserType,_IPAddress,_CreatedBy));
  SELECT LAST_INSERT_ID() AS ID;
  ELSE 
      UPDATE sssm_config 
      SET 
      CrashEmail        = IFNULL(_CrashEmail,CrashEmail),
      SupportEmail      = IFNULL(_SupportEmail,SupportEmail),
      TimeZone      = IFNULL(_TimeZone,TimeZone),
      AppVersionAndroid      = IFNULL(_AppVersionAndroid,AppVersionAndroid),
      AppVersionIOS      = IFNULL(_AppVersionIOS,AppVersionIOS),
      ModifiedDate      = CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone), 
      ModifiedBy        = IFNULL(_ModifiedBy,ModifiedBy)
      WHERE ConfigID    =   _ConfigID;
      SET @tmp = CONCAT('EditConfig',' has updated config.');
      SET @ID=(SELECT Fn_A_AddActivityLog('EditConfig',@tmp,_UserType,_IPAddress,_ModifiedBy));
      SELECT _ConfigID AS ID;
  END IF;
END$$

