        IFNULL(_ActionUser,''),
        IFNULL(_Subject,''),
        IFNULL(_Message,''),
        IFNULL(_Attachment,''),
        CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone),
        IFNULL(_RemindedBy,0),
        IFNULL(_RemindedBy,0),
        CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone)
    );
    
    SET @LAST_ID = LAST_INSERT_ID();
    IF(_ActionUser = "Customer") THEN 
        SET @Title = "AddCustomerAction";
        SET @customerID = _ID;
    SET @customername = (SELECT CONCAT(C.FirstName,' ',C.LastName) FROM sssm_customer C WHERE C.CustomerID=@customerID);
        SET @Type= 'Customer';
        SET @_Text= 'customer';
    ELSEIF(_ActionUser = "CustomerProperty") THEN 
        SET @Title = "AddCustomerPropertyAction";
    SET @customerID = (SELECT C.CustomerID FROM sssm_customerproperty C WHERE C.CustomerPropertyID=_ID);
    SET @customername = (SELECT CONCAT(C.FirstName,' ',C.LastName) FROM sssm_customer C WHERE C.CustomerID=@customerID);
        SET @Type= 'Customer';
        SET @_Text= 'customer';
    ELSEIF(_ActionUser = "CustomerReminder") THEN 
        SET @Title = "AddCustomerReminderAction";
    SET @customerID = (SELECT CP.CustomerID FROM sssm_customerreminder C INNER JOIN sssm_customerproperty CP ON CP.CustomerPropertyID = C.CustomerPropertyID WHERE C.CustomerReminderID=_ID LIMIT 1);
    SET @customername = (SELECT CONCAT(C.FirstName,' ',C.LastName) FROM sssm_customer C WHERE C.CustomerID=@customerID);
        SET @Type= 'Customer';
        SET @_Text= 'customer';
        
    ELSEIF(_ActionUser = "Visitor") THEN 
        SET @Title = "AddVisitorAction";
    SET @customerID = _ID;
    SET @customername = (SELECT CONCAT(C.FirstName,' ',C.LastName) FROM sssm_visitor C WHERE C.VisitorID=@customerID);
        SET @Type= 'Visitor';
        SET @_Text= 'Visitor';
    ELSEIF(_ActionUser = "VisitorReminder") THEN 
        SET @Title = "AddVisitorReminderAction";
    SET @customerID = (SELECT C.VisitorID FROM sssm_visitorreminder C WHERE C.VisitorReminderID=_ID);
    SET @customername = (SELECT CONCAT(C.FirstName,' ',C.LastName) FROM sssm_visitor C WHERE C.VisitorID=@customerID);
        SET @Type= 'Visitor';
        SET @_Text= 'Visitor';
    END IF;



SET @adminname = (SELECT CONCAT(A.FirstName,' ',A.LastName) FROM sssm_admindetails A WHERE A.UserID=_RemindedBy);


SET @process = (SELECT Fn_A_AddCustomerProcess(@customerID,@Type, CONCAT(IFNULL(@adminname,'Employee') , ' has send reminder to ',IFNULL(@_Text,' Customer'),' (', IFNULL(@customername,'Customer'),').'), _RemindedBy, @LAST_ID, CONCAT(_ActionUser,'Action')) as addProcess);

    SET @ID=(SELECT Fn_A_AddActivityLog(@Title,CONCAT(' has send reminder to ',IFNULL(@_Text,' Customer'),' (', IFNULL(@customername,'Customer'),').'),_UserType,_IPAddress,_RemindedBy));
    SET @ID = (SELECT UserReminderID FROM sssm_reminderaction WHERE ReminderActionID = @LAST_ID);
    IF(_ActionUser = "Customer") THEN 
        SET @Count = (SELECT COUNT(CustomerID) FROM sssm_customer WHERE CustomerID = @ID);
        IF(@Count > 0) THEN
            SELECT 
                @LAST_ID AS ID,
                IFNULL(C.CustomerID,0) AS CustomerID,
                IFNULL(C.FirstName,'') AS FirstName,
                IFNULL(C.LastName,'') AS LastName,
                IFNULL(C.EmailID,'') AS EmailID,
                IFNULL(C.MobileNo,'') AS MobileNo,
                _ActionUser AS ActionUser
            FROM 
                sssm_customer C
            WHERE 
                C.CustomerID = @ID;
        ELSE 
            SELECT Fn_M_getErrorMessage('user_details_not_found') AS Message;
        END IF;
    ELSEIF(_ActionUser = "CustomerProperty") THEN 
        SET @CID = (SELECT IFNULL(CustomerID,0) FROM sssm_customerproperty WHERE CustomerPropertyID = @ID);

        SET @Count = (SELECT COUNT(CustomerID) FROM sssm_customer WHERE CustomerID = @ID);
        IF(@Count > 0) THEN
            SELECT 
                @LAST_ID AS ID,
                IFNULL(C.CustomerID,0) AS CustomerID,
                IFNULL(C.FirstName,'') AS FirstName,
                IFNULL(C.LastName,'') AS LastName,
                IFNULL(C.EmailID,'') AS EmailID,
                IFNULL(C.MobileNo,'') AS MobileNo,
                _ActionUser AS ActionUser
            FROM 
                sssm_customer C
            WHERE 
                C.CustomerID = @CID;
        ELSE 
            SELECT Fn_M_getErrorMessage('user_details_not_found') AS Message;
        END IF;
    ELSEIF(_ActionUser = "CustomerReminder") THEN 
        SET @CPID = (SELECT IFNULL(CustomerPropertyID,0) FROM sssm_customerreminder WHERE CustomerReminderID = @ID);
        SET @CID = (SELECT IFNULL(CustomerID,0) FROM sssm_customerproperty WHERE CustomerPropertyID = @CPID);
        SET @Count = (SELECT COUNT(CustomerID) FROM sssm_customer WHERE CustomerID = @CID);
        IF(@Count > 0) THEN
            SELECT 
                @LAST_ID AS ID,
                IFNULL(C.CustomerID,0) AS CustomerID,
                IFNULL(C.FirstName,'') AS FirstName,
                IFNULL(C.LastName,'') AS LastName,
                IFNULL(C.EmailID,'') AS EmailID,
                IFNULL(C.MobileNo,'') AS MobileNo,

                _ActionUser AS ActionUser
            FROM 
                sssm_customer C
            WHERE 
                C.CustomerID = @CID;
        ELSE 
            SELECT Fn_M_getErrorMessage('user_details_not_found') AS Message;
        END IF;
    ELSEIF(_ActionUser = "Visitor") THEN 
        SET @Count = (SELECT COUNT(VisitorID) FROM sssm_visitor WHERE VisitorID = @ID);
        IF(@Count > 0) THEN
            SELECT 
                @LAST_ID AS ID,
                IFNULL(V.VisitorID,0) AS VisitorID,
                IFNULL(V.FirstName,'') AS FirstName,
                IFNULL(V.LastName,'') AS LastName,
                IFNULL(V.EmailID,'') AS EmailID,
                IFNULL(V.MobileNo,'') AS MobileNo,
                _ActionUser AS ActionUser
            FROM 
                sssm_visitor V
            WHERE 
                V.VisitorID = @ID;
        ELSE
            SELECT Fn_M_getErrorMessage('user_details_not_found') AS Message;
        END IF;
    ELSEIF(_ActionUser = "VisitorReminder") THEN 
        SET @VID = (SELECT VisitorID FROM sssm_visitorreminder WHERE VisitorReminderID = @ID);
        SET @Count = (SELECT COUNT(VisitorID) FROM sssm_visitor WHERE VisitorID = @VID);
        IF(@Count > 0) THEN
            SELECT 
                @LAST_ID AS ID,
                IFNULL(V.VisitorID,0) AS VisitorID,
                IFNULL(V.FirstName,'') AS FirstName,
                IFNULL(V.LastName,'') AS LastName,
                IFNULL(V.EmailID,'') AS EmailID,
                IFNULL(V.MobileNo,'') AS MobileNo,
                _ActionUser AS ActionUser
            FROM 
                sssm_visitor V
            WHERE 
                V.VisitorID = @VID;
        ELSE
            SELECT Fn_M_getErrorMessage('user_details_not_found') AS Message;
        END IF;
    END IF;

COMMIT;
END$$

