        IF(@email_exist = 1 OR @mobile_exist = 1) THEN
          IF(@email_exist = 1) THEN
            SELECT Fn_M_getErrorMessage('emailid_already_exist') AS Message;
          ELSE
            SELECT Fn_M_getErrorMessage('mobileno_already_exist') AS Message;
          END IF;
        ELSE

            INSERT INTO sssm_visitor (    
                EmployeeID,
                OpportunityID,
                FirstName,
                LastName,
                MobileNo,
                Address,
                EmailID,
                CompanyName,
                DesignationID,
                BirthDate,
                BirthMonth,
                AnniversaryDate,
                AnniversaryMonth,
                SecondMobileNo,
                SecondName,
                CreatedDate,
                CreatedBy,
                Status
                )
          VALUES (
                IFNULL(_EmployeeID,0),
                IFNULL(_OpportunityID,0),
                IFNULL(_FirstName,''),
                IFNULL(_LastName,''),
                IFNULL(_MobileNo,''),
                IFNULL(_Address,''),
                IFNULL(_EmailID,''),
                IFNULL(_CompanyName,''),
                IFNULL(_DesignationID,0),
                IFNULL(_BirthDate,0),
                IFNULL(_BirthMonth,0),
                IFNULL(_AnniversaryDate,0),
                IFNULL(_AnniversaryMonth,0),
                IFNULL(_SecondMobileNo,''),
                IFNULL(_SecondName,''),
                CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone),
                IFNULL(_CreatedBy,0),
                IFNULL(_Status,0)
              );

            IF(_OpportunityID>0) THEN
              UPDATE ss_opportunity SET
                EmployeeID=IFNULL(_EmployeeID,0),
                IsVisitor='1',
                ConvertDateTime=CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone)
              WHERE OpportunityID=_OpportunityID;
            END IF;


            SET @insertID = LAST_INSERT_ID();
                   
            SELECT 
              @insertID AS ID,
              IFNULL(V.VisitorID,0) AS VisitorID, 
              IFNULL(V.EmployeeID,0) AS EmployeeID,
              IFNULL(A.FirstName,'') AS EmployeeFirstName,
              IFNULL(A.LastName,'') AS EmployeeLastName,
              CONCAT(IFNULL(A.FirstName,''),' ',IFNULL(A.LastName,'')) AS EmployeeName,
              IFNULL(V.FirstName,'') AS FirstName,
              IFNULL(V.LastName,'') AS LastName, 
              IFNULL(V.EmailID,'') AS EmailID,
              IFNULL(V.MobileNo,'') AS MobileNo, 
              IFNULL(V.Address,'') AS Address,
              IFNULL(V.CreatedBy,0) AS CreatedBy,
              Fn_A_ChangeDateTimeFormat(V.CreatedDate) AS CreatedDate,
              IFNULL(V.Status,0) AS Status,
              IFNULL(V.IsIdle,0) AS IsIdle,
              IFNULL(V.IsCustomer,0) AS IsVisitorConvert,
              Fn_M_getErrorMessage('visitor_added_successfully') as Message,
              Fn_A_AddCustomerProcess(V.VisitorID,'Visitor', CONCAT(IFNULL(A.FirstName,''),' ',IFNULL(A.LastName,''), ' has added ',IFNULL(V.FirstName,''),' ',IFNULL(V.LastName,''),' as a visitor.'), _CreatedBy, V.VisitorID, 'AddVisitor') as addProcess,
