            FROM sssm_visitor V
            INNER JOIN sssm_admindetails A ON A.UserID = V.EmployeeID
            LEFT JOIN sssm_designation D ON D.DesignationID = V.DesignationID
            WHERE V.VisitorID = @insertID;
        END IF;
   COMMIT;
END$$

CREATE DEFINER=`` PROCEDURE `usp_A_AddVisitorIdle` (IN `_ID` INT, IN `_UserID` INT, IN `_UserType` ENUM('Admin Web','Admin Android','Admin IOS','Employee Web','Employee Android','Employee IOS'), IN `_IPAddress` VARCHAR(16), IN `_IsIdle` INT)  NO SQL BEGIN 
SET @Count= (SELECT COUNT(VisitorID) FROM sssm_visitor WHERE VisitorID = _ID);

IF(@Count > 0) THEN
    SET @Cn = (SELECT COUNT(VisitorID) FROM sssm_visitor WHERE VisitorID = _ID AND IsIdle = _IsIdle);
    IF(@Cn > 0)THEN
        SELECT Fn_M_getErrorMessage('old_new_status_are_same_not_updated') AS Message;
    ELSE
        UPDATE sssm_visitor
        SET 
            IsIdle = _IsIdle
        WHERE 
            VisitorID = _ID;
        SET @adminname = (SELECT CONCAT(IFNULL(A.FirstName,''),' ',IFNULL(A.LastName,'')) FROM sssm_admindetails A WHERE A.UserID=_UserID LIMIT 1);
