        SELECT _ID AS ID,  Fn_A_AddCustomerProcess(_ID,'Visitor', CONCAT(@adminname , ' has added ', @VisitorName,'(visitor) As Idle.'), _UserID, _ID, 'VisitorIdle') as addProcess,_IsIdle AS IsIdle;
    END IF;
ELSE
    SELECT Fn_M_getErrorMessage('visitor_not_found') AS Message;
END IF;
END$$

