      Fn_A_AddCustomerProcess(V.VisitorID,'Visitor', CONCAT(IFNULL(A.FirstName,''),' ',IFNULL(A.LastName,''), ' has added ',IFNULL(V.FirstName,''),' ',IFNULL(V.LastName,''),' as a visitor.'), _CreatedBy, V.VisitorID, 'AddVisitor') as addProcess,
