    WHERE 
        CustomerPropertyID = _ID;
    SET @PropertyAmount = (SELECT Amount FROM sssm_customerproperty WHERE CustomerPropertyID = _ID);
    SET @PropertyGSTAmount = (SELECT GSTAmount FROM sssm_customerproperty WHERE CustomerPropertyID = _ID);
    SET @ReceiveAmount = (SELECT Fn_TotnoOfPayByCustomer(_ID));
    SET @ReceiveGSTAmount = (SELECT Fn_GetGSTAmount(_ID,0));
    INSERT INTO sssm_cancelproperty(
            CustomerPropertyID,
            PropertyAmount,
            PropertyGSTAmount,
            ReceiveAmount,
            ReceiveGSTAmount,
            IsCancelFee,
            CancelFeeAmount,
            RefundAmount,
            RefundGSTAmount,
            Reason,
            CreatedBy,
            CreatedDate
        )
        VALUES(
            _ID,
            @PropertyAmount,
            @PropertyGSTAmount,
            @ReceiveAmount,
            @ReceiveGSTAmount,
            _IsCancelFee,
            _CancelFeeAmount,
            _RefundAmount,
            _RefundGSTAmount,
            _Reason,
            _UserID,
            CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone)
            );
        UPDATE sssm_customerpayment
        SET Status = 0
        WHERE 
            CustomerPropertyID = _ID;
        SET @adminname = (SELECT CONCAT(IFNULL(A.FirstName,''),' ',IFNULL(A.LastName,'')) FROM sssm_admindetails A WHERE A.UserID=_UserID LIMIT 1);
        SET @CustomerID = (SELECT CustomerID FROM sssm_customerproperty WHERE CustomerPropertyID = _ID);
        SET @CustomerName = (SELECT CONCAT(FirstName,' ',LastName) FROM sssm_customer WHERE  CustomerID =  @CustomerID LIMIT 1);
        
        SET @ID=(SELECT Fn_A_AddActivityLog('CancelledProperty',CONCAT(' has cancelled property of ', @CustomerName,'.'),_UserType,_IPAddress,_UserID));

        SELECT _ID AS ID, Fn_A_AddCustomerProcess(@CustomerID,'Customer', CONCAT(@adminname , ' has cancelled property of ', @CustomerName,'.'), _UserID, _ID, 'CancelledProperty') as addProcess;
END$$

