  IF(@ResultCount > 0) THEN
    SET @VERFLAG = (SELECT COUNT(UserID) FROM sssm_admindetails WHERE (EmailID = _EmailID OR MobileNo = _EmailID) AND IsAllow = 1);
    IF(@VERFLAG > 0) THEN
      SET @CNT = (SELECT COUNT(UserID) FROM sssm_admindetails WHERE (EmailID = _EmailID OR MobileNo = _EmailID) AND Password = _Password);
      IF(@CNT > 0)THEN 
        SELECT 
          IFNULL(UserID,0) AS UserID,
