ELSEIF(_FilterType = "Yearly")THEN
  SET @StartDate = (SELECT MAKEDATE(YEAR(NOW()),1));
  SET @EndDate = (SELECT LAST_DAY(DATE_ADD(NOW(), INTERVAL 12-MONTH(NOW()) MONTH)));
ELSEIF(_FilterType = "Other")THEN
  SET @StartDate = _CustomStartDate;
  SET @EndDate = _CustomEndDate;
