CREATE DEFINER=`` PROCEDURE `usp_A_EditCustomerPayment` (IN `_ID` INT, IN `_MileStone` TEXT, IN `_CustomerPropertyID` INT, IN `_AmountType` INT, IN `_PaymentAmount` INT, IN `_GSTAmount` INT, IN `_PaymentDate` DATE, IN `_PaymentMode` ENUM('Cheque','Online','Cash'), IN `_ChequeNo` VARCHAR(6), IN `_IFCCode` VARCHAR(16), IN `_AccountNo` VARCHAR(25), IN `_BankName` VARCHAR(250), IN `_BranchName` VARCHAR(100), IN `_ModifiedBy` INT, IN `_Status` INT, IN `_UserType` ENUM('Admin Web','Admin Android','Admin IOS','Employee Web','Employee Android','Employee IOS'), IN `_IPAddress` VARCHAR(16), IN `_UTR` VARCHAR(50))  NO SQL BEGIN
DECLARE flag INT;
DECLARE EXIT handler for sqlexception
    BEGIN
        
        GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, @errno = MYSQL_ERRNO, @text = MESSAGE_TEXT; 
        SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
        SELECT "Edit Customer Payment" AS Method , @full_error AS Message;
        ROLLBACK;
    END;
    START TRANSACTION;
    SET  @TimeZone = (SELECT TimeZone  FROM sssm_config Limit 1);
    IF(@TimeZone = "") THEN SET @TimeZone = "+00:00"; END IF;
    SET @oldTotalPayment = (SELECT Fn_TotnoOfPayByCustomer(_CustomerPropertyID));
    SET @oldGSTRemaining = (Fn_GetGSTAmount(_CustomerPropertyID,1));
    SET @oldTotal = (@oldTotalPayment+@oldGSTRemaining);
    SET flag = (SELECT count(CustomerPaymentID) from  sssm_customerpayment WHERE  CustomerPaymentID = _ID);
    IF(flag > 0) THEN
    IF(_CustomerPropertyID = 0) THEN SET _CustomerPropertyID = NULL; END IF;
    SET @_Percentage = (SELECT ((_PaymentAmount/Amount)*100) as pre FROM sssm_customerproperty WHERE CustomerPropertyID = _CustomerPropertyID);
UPDATE sssm_customerpayment SET
    CustomerPropertyID = IFNULL(_CustomerPropertyID,CustomerPropertyID),
    MileStone = IFNULL(_MileStone,MileStone),
    AmountType = IFNULL(_AmountType,AmountType),
    PaymentAmount = IFNULL(_PaymentAmount,PaymentAmount),
    GSTAmount = IFNULL(_GSTAmount,GSTAmount),
    Percentage = @_Percentage,
    PaymentDate = IFNULL(_PaymentDate,PaymentDate),
    PaymentMode = IFNULL(_PaymentMode,PaymentMode),
    ChequeNo = IFNULL(_ChequeNo,ChequeNo),
    IFCCode = IFNULL(_IFCCode,IFCCode),
    UTR = IFNULL(_UTR,UTR),
    AccountNo = IFNULL(_AccountNo,AccountNo),
    BankName = IFNULL(_BankName,BankName),
    BranchName = IFNULL(_BranchName,BranchName),
    ModifiedBy = IFNULL(_ModifiedBy,0),
    ModifiedDate = CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone),
    Status = IFNULL(_Status,Status)
    WHERE CustomerPaymentID = _ID;

SET @adminname = (SELECT CONCAT(A.FirstName,' ',A.LastName) FROM sssm_admindetails A WHERE A.UserID=_ModifiedBy);
SET @CustomerID = (SELECT CP.CustomerID FROM sssm_customerproperty CP WHERE CP.CustomerPropertyID = _CustomerPropertyID);
SET @customername = (SELECT CONCAT(C.FirstName,' ',C.LastName) FROM sssm_customer C WHERE C.CustomerID=@CustomerID);
SET @ID=(SELECT Fn_A_AddActivityLog('EditCustomerPayment',CONCAT(' has edit propery payment of customer (', @customername, ').'), _UserType, _IPAddress, _ModifiedBy));


    SET @paymentPush = '';
    SET @TotPayment = (SELECT Fn_TotnoOfPayByCustomer(_CustomerPropertyID));
    SET @GSTRemaining = (Fn_GetGSTAmount(_CustomerPropertyID,1));
    SET @TAmount = (SELECT Amount FROM sssm_customerproperty WHERE CustomerPropertyID = _CustomerPropertyID);
    SET @NewTotal = (@GSTRemaining+@TotPayment);
    SET @IsATS = (SELECT IsATS FROM sssm_customerproperty WHERE CustomerPropertyID = _CustomerPropertyID);
    SET @Percentage = (SELECT PJ.ATSPercentage FROM sssm_customerproperty CP
                INNER JOIN sssm_property P ON (P.PropertyID = CP.PropertyID)
                INNER JOIN sssm_project PJ ON (PJ.ProjectID = P.ProjectID)
                WHERE 
                    CP.CustomerPropertyID = _CustomerPropertyID);
    SET @PerAmount = (@TAmount * @Percentage) / 100;
    IF(@oldTotalPayment < @PerAmount AND @PerAmount <= @TotPayment AND @IsATS!=1) THEN
        SET @paymentPush = 'ATS';
    END IF;
    SET @sd_TAmount = (SELECT Amount FROM sssm_customerproperty WHERE CustomerPropertyID = _CustomerPropertyID);
    SET @IsSD = (SELECT IsATS FROM sssm_customerproperty WHERE CustomerPropertyID = _CustomerPropertyID);
    IF(@sd_TAmount = @TotPayment AND @NewTotal != @oldTotal AND @IsSD!=1 AND @GSTRemaining = 0) THEN
        SET @paymentPush = 'SD';
    END IF;
    SELECT Z.*,  Fn_A_AddCustomerProcess(@CustomerID,'Customer', CONCAT(@adminname , ' has edit propery payment of customer (', @customername,').'), _ModifiedBy, _ID, 'EditCustomerPayment') as addProcess
    FROM (SELECT 
          IFNULL((SELECT A.FirstName FROM sssm_admindetails A WHERE A.UserID=_ModifiedBy),'') AS EmployeeFirstName,
          IFNULL((SELECT A.LastName FROM sssm_admindetails A WHERE A.UserID=_ModifiedBy),'') AS EmployeeLastName,

          IFNULL(C.FirstName,'') AS FirstName,
          IFNULL(C.LastName,'') AS LastName,
          IFNULL(C.EmailID,'') AS EmailID,
          IFNULL(C.MobileNo,'') AS MobileNo,
          IFNULL(C.MobileNo1,'') AS MobileNo1,
          IFNULL(C.Address,'') AS Address,

        IFNULL(CP.CustomerPropertyID,0) AS CustomerPropertyID,
        IFNULL(CP.CustomerID,0) AS CustomerID,
        IFNULL(CP.PropertyID,0) AS PropertyID,
        Fn_A_ChangeDateFormat(CP.PurchaseDate) AS PurchaseDate,
        IFNULL(CP.SerialNo,'') AS SerialNo,
        IFNULL(CP.Amount,0) AS Amount,
        IFNULL(CP.GSTAmount,0) AS GSTAmount,
        IFNULL(CP.CustomerFirstName,'') AS CustomerFirstName,
        IFNULL(CP.CustomerLastName,'') AS CustomerLastName,
        IFNULL(CP.CustomerAddress,'') AS CustomerAddress,
        IFNULL(CP.CustomerEmailID,'') AS CustomerEmailID,
        IFNULL(CP.CustomerPanNo,'') AS CustomerPanNo,
        IFNULL(CP.CustomerAdhaarNo,'') AS CustomerAdhaarNo,
        IFNULL(CP.CustomerMobileNo,'') AS CustomerMobileNo,
        IFNULL(CP.CustomerMobileNo1,'') AS CustomerMobileNo1,
        IFNULL(CP.CustomerSFirstName,'') AS CustomerSFirstName,
        IFNULL(CP.CustomerSLastName,'') AS CustomerSLastName,
        IFNULL(CP.CustomerSAddress,'') AS CustomerSAddress,
        IFNULL(CP.CustomerSEmailID,'') AS CustomerSEmailID,
        IFNULL(CP.CustomerSPanNo,'') AS CustomerSPanNo,
        IFNULL(CP.CustomerSAdhaarNo,'') AS CustomerSAdhaarNo,
        IFNULL(CP.CustomerSMobileNo,'') AS CustomerSMobileNo,
        IFNULL(CP.CustomerSMobileNo1,'') AS CustomerSMobileNo1,
        IFNULL(CP.CreatedBy,0) AS CreatedBy,
        Fn_A_ChangeDateTimeFormat(CP.CreatedDate) AS CreatedDate,
        IFNULL(CP.ModifiedBy,0) AS ModifiedBy,
        IFNULL(CP.ModifiedDate,'') AS ModifiedDate,
        IFNULL(CP.Status,0) AS Status,
        IFNULL(PT.PropertyNo,'') AS PropertyNo,
        IFNULL(PJ.Title,'') AS Title,
        IFNULL(PJ.ProjectID,'0') AS ProjectID,
        IFNULL(PJ.Location,'') AS Location,
        CONCAT(Fn_GetPropertyPic(CP.PropertyID)) AS PropertyImage,
        (Fn_TotremPayByCustomer(CP.CustomerPropertyID)) AS RemainingPayment,
        (Fn_TotnoOfPayByCustomer(CP.CustomerPropertyID)) AS TotalPayment,
        (Fn_TotnoOfPayment(CP.CustomerPropertyID)) AS TotalNoOfPayment,
        (Fn_GetGSTAmount(CP.CustomerPropertyID,1)) AS RemainingGSTPayment,
        (Fn_GetGSTAmount(CP.CustomerPropertyID,0)) AS TotalGSTPayment,
        IFNULL(PJ.ATSPercentage,0) AS ATSPercentage,
        IFNULL(CP.ChannelPartner,'') AS ChannelPartner, 
        IFNULL(CP.IsVerified,'') AS IsVerified, 
        IFNULL(CP.IsATS,'') AS IsATS, 
        IFNULL(CP.IsSD,'') AS IsSD,
        IFNULL(CP.IsHold,'') AS IsHold,
        _CustomerPropertyID AS ID,
        @paymentPush as PaymentPush
      FROM 
        sssm_customerproperty CP
        INNER JOIN sssm_customer C ON C.CustomerID=CP.CustomerID
      LEFT JOIN sssm_property PT ON(PT.PropertyID = CP.PropertyID)
      LEFT JOIN sssm_project PJ ON(PJ.ProjectID = PT.ProjectID)
      WHERE 
        CP.CustomerPropertyID = _CustomerPropertyID) Z ;


ELSE

    SELECT Fn_M_getErrorMessage('customerpayment_not_found') AS Message; 
END IF;
COMMIT;
END$$

