CREATE DEFINER=`` PROCEDURE `usp_A_GetActivityLog` (IN `PageSize` INT, IN `CurrentPage` INT, IN `pActivitylogName` VARCHAR(200) CHARSET utf8, IN `pDate` DATE)  NO SQL BEGIN
