              WHERE ca.MethodName  LIke CONCAT('%',IFNULL(pActivitylogName,ca.MethodName),'%')
              ORDER BY ca.CreatedDate DESC;
      END IF;
ELSE
  SET @rownum:=0;
  IF pDate <> '' THEN
    SELECT AA.*,@rownum AS rowcount FROM (
      SELECT V.*, @rownum := @rownum + 1 AS Rno
      FROM (
      SELECT  
          IFNULL(ca.ActivityLogID,0) AS CustomerActivityID, 
          IFNULL(ca.MethodName,'') AS MethodName ,
          IFNULL(ca.ActivityDescription,'') AS ActivityDescription , 
          IFNULL(ca.Status,0) AS Status ,
