IF(_Status = -1) THEN SET _Status =NULL; END IF;
SET @Count = (SELECT 
                COUNT(C.UserID) 
              FROM 
                sssm_chanelpartner C
              INNER JOIN sssm_user U ON U.UserID=C.UserID
              WHERE 
                CONCAT(C.FirstName,' ',C.LastName) LIKE CONCAT("%",IFNULL(_FullName,IFNULL(C.FirstName,C.LastName)),"%") AND
                C.FirmName LIKE CONCAT('%',IFNULL(_FirmName,C.FirmName),'%') AND
                U.MobileNo LIKE CONCAT('%',IFNULL(_MobileNo,U.MobileNo),'%') AND
                C.Status = IFNULL(_Status,C.Status)
              );
IF(_PageSize = -1)THEN
  SET _PageSize = @Count * 10;
  SET _CurrentPage = 1;
END IF;
IF(@Count > 0) THEN 
  SET @rownum:=0;
  SELECT AA.*,@rownum AS rowcount FROM (
    SELECT V.*, @rownum := @rownum + 1 AS Rno FROM ( 
     SELECT 
        IFNULL(C.ChanelPartnerID,0) AS ChanelPartnerID,
        IFNULL(C.FirmName,'')  AS FirmName,
        IFNULL(C.FirstName,'')  AS FirstName,
        IFNULL(C.LastName,'')  AS LastName,
        IFNULL(C.PanCard,0) AS PanCard,
        IFNULL(C.AadharCard,0) AS AadharCard,
        IFNULL(C.GSTNumber,0) AS GSTNumber,
        IFNULL(C.BankAccount,0) AS BankAccount,
        IFNULL(U.EmailID,'') AS EmailID,
        IFNULL(U.MobileNo,0) AS MobileNo,
        IFNULL(C.Status,0) AS Status,
        IFNULL(C.BankName,'') AS BankName,
        IFNULL(C.IFCCode,0) AS IFCCode,
        IFNULL(C.ReraCode,0) AS ReraCode,
        COUNT(FM.FamilyMemberID) AS FamilyMemberCount
      FROM 
        sssm_chanelpartner C
      INNER JOIN sssm_user U ON U.UserID=C.UserID   
      LEFT JOIN sssm_familymembers FM on FM.ChanelPartnerID = C.ChanelPartnerID
      WHERE 
          CONCAT(C.FirstName,' ',C.LastName) LIKE CONCAT("%",IFNULL(_FullName,IFNULL(C.FirstName,C.LastName)),"%") AND
          C.FirmName LIKE CONCAT('%',IFNULL(_FirmName,C.FirmName),'%') AND
