CREATE DEFINER=`` PROCEDURE `usp_A_GetCity` (IN `PageSize` INT, IN `CurrentPage` INT, IN `_city` VARCHAR(250) CHARSET utf8, IN `_state` INT, IN `status_search` INT, IN `_country` INT)  NO SQL BEGIN
