      	FROM sssm_cities s
      	INNER JOIN sssm_state c ON c.StateID = s.StateID
      	INNER JOIN sssm_country ct ON ct.CountryID = c.CountryID
      	WHERE 
      		s.CityName LIKE CONCAT('%',IFNULL(_city,s.CityName),'%') AND
