   SET @rownum:=0;

 SELECT AA.*,@rownum AS rowcount FROM (
        SELECT V.*, @rownum := @rownum + 1 AS Rno
        FROM ( 
		SELECT 
	       IFNULL(s.CityID,0) AS CityID,
	       IFNULL(s.CityName,'') AS CityName, 
	       IFNULL(s.StateID,'') AS StateID, 
	       IFNULL(c.StateName,'') AS StateName, 
