CREATE DEFINER=`` PROCEDURE `usp_A_GetCustomer` (IN `_PageSize` INT, IN `_CurrentPage` INT, IN `_Name` VARCHAR(200), IN `_MobileNo` VARCHAR(15), IN `_Status` INT, IN `_EmailID` VARCHAR(250), IN `_RoleID` INT, IN `_ProjectID` INT, IN `_Type` VARCHAR(50), IN `_PropertyID` INT)  NO SQL BEGIN 
  IF(_RoleID = -1) THEN SET _RoleID = NULL;    END IF;
  IF(_RoleID = -2) THEN SET _RoleID = NULL;    END IF;
  IF(_ProjectID = -1) THEN SET _ProjectID = NULL; END IF;
  IF(_PropertyID = -1) THEN SET _PropertyID = NULL; END IF;
  IF(_Status = -1)    THEN SET _Status = NULL;    END IF;
  IF(_Name ='')       THEN SET _Name = NULL;      END IF;
  IF(_MobileNo='')    THEN SET _MobileNo = NULL;  END IF;
  IF(_MobileNo='-1')  THEN SET _MobileNo = NULL;  END IF;
IF(_Type = "Web") THEN
  SET @ModuleID = 17;
ELSE
  SET @ModuleID = 35;
END IF;
SET @Projects = _ProjectID;
  IF(_RoleID IS NOT NULL)THEN 
    IF(_ProjectID IS NULL) THEN 
      SET @Projects = (SELECT GROUP_CONCAT(P.ProjectID) FROM sssm_project P 
                      INNER JOIN sssm_roleproject RP ON (RP.ProjectID = P.ProjectID)
                      INNER JOIN sssm_rolemap RM ON (RM.RoleProjectID = RP.RoleProjectID AND RM.is_view = 1 AND RM.ModuleID = @ModuleID)
                      WHERE 
                        RP.RoleID = _RoleID AND P.Status = 1);
    ELSE
      SET @RoleProjectID = (SELECT RoleProjectID FROM sssm_roleproject WHERE RoleID = _RoleID AND ProjectID = _ProjectID);
      SET @Flag = (SELECT IFNULL(is_view,0) FROM sssm_rolemap WHERE RoleProjectID = @RoleProjectID AND ModuleID = @ModuleID);
      IF(@Flag = 0) THEN
        SET @Projects = NULL;
      END IF;
    END IF;
  ELSE 
    IF(_ProjectID IS NULL) THEN 
      SET @Projects = (SELECT GROUP_CONCAT(ProjectID) FROM sssm_project WHERE Status = 1);
    END IF;
  END IF;
  SET @cnt = (SELECT COUNT(C.CustomerID) FROM sssm_customer C
        LEFT JOIN sssm_customerproperty CP ON (CP.CustomerID = C.CustomerID)
        LEFT JOIN sssm_property PR ON(PR.PropertyID = CP.PropertyID)
  WHERE 
      CONCAT(C.FirstName,' ',C.LastName) LIKE CONCAT("%",IFNULL(_Name,IFNULL(C.FirstName,C.LastName)),"%") AND
      C.EmailID LIKE CONCAT("%",_EmailID,"%") AND
      (C.MobileNo LIKE CONCAT("%",IFNULL(_MobileNo,C.MobileNo),"%") OR
      C.MobileNo1 LIKE CONCAT("%",IFNULL(_MobileNo,C.MobileNo1),"%") )AND
      C.Status = IFNULL(_Status,C.Status) AND 
      ( 
        FIND_IN_SET(PR.ProjectID,@Projects) OR  PR.ProjectID IS NULL
      ) AND
      IFNULL(CP.PropertyID,0) = IFNULL(_PropertyID,IFNULL(CP.PropertyID,0))
  );
  
  IF(@cnt > 0) THEN 
    IF(_PageSize = -1) THEN
      SELECT  
        IFNULL(C.CustomerID,0) AS CustomerID,
        (Fn_A_GetPropertyDetails(C.CustomerID,@Projects)) AS PropertyDetails,
        (Fn_LatestPurchaseDateByCustomerID(C.CustomerID)) AS LatestPurchaseDate,
        IFNULL(C.FirstName,'') AS FirstName,
        IFNULL(C.LastName,'') AS LastName,
        IFNULL(C.EmailID,'') AS EmailID,
        IFNULL(C.MobileNo,'') AS MobileNo,
        IFNULL(C.MobileNo1,'') AS MobileNo1,
        IFNULL(C.Address,'') AS Address,
        IFNULL(DATE_FORMAT(C.CreatedDate,"%d-%m-%Y %H:%i:%s"),'') AS CreatedDate,
        IFNULL(C.Status,'') AS Status,
        IFNULL(CONCAT(AD.FirstName," ",AD.LastName),' ') AS EmployeeName
      FROM sssm_customer C
      INNER JOIN sssm_admindetails AD  ON AD.UserID=C.EmployeeID
      LEFT JOIN sssm_customerproperty CP ON (CP.CustomerID = C.CustomerID)
      LEFT JOIN sssm_property PR ON(PR.PropertyID = CP.PropertyID)
      WHERE 
        CONCAT(C.FirstName,' ',C.LastName) LIKE CONCAT("%",IFNULL(_Name,IFNULL(C.FirstName,C.LastName)),"%") AND
        C.EmailID LIKE CONCAT("%",_EmailID,"%") AND
        ( C.MobileNo LIKE CONCAT("%",IFNULL(_MobileNo,C.MobileNo),"%") OR
        C.MobileNo1 LIKE CONCAT("%",IFNULL(_MobileNo,C.MobileNo1),"%") )AND
        C.Status = IFNULL(_Status,C.Status) AND 
        ( 
          FIND_IN_SET(PR.ProjectID,@Projects) OR  PR.ProjectID IS NULL
        )AND
        IFNULL(CP.PropertyID,0) = IFNULL(_PropertyID,IFNULL(CP.PropertyID,0))
      GROUP BY C.CustomerID
      ORDER BY LatestPurchaseDate DESC;
    ELSE
      SET @rownum:=0;
      SELECT AA.* ,@rownum AS rowcount FROM (
        SELECT V.*, @rownum := @rownum + 1 AS Rno

        FROM (
              SELECT  
                IFNULL(C.CustomerID,0) AS CustomerID,
                (Fn_A_GetPropertyDetails(C.CustomerID,@Projects)) AS PropertyDetails,
                (Fn_LatestPurchaseDateByCustomerID(C.CustomerID)) AS LatestPurchaseDate,
                IFNULL(C.FirstName,'') AS FirstName,
                IFNULL(C.LastName,'') AS LastName,
                IFNULL(C.EmailID,'') AS EmailID,
                IFNULL(C.MobileNo,'') AS MobileNo,
                IFNULL(C.MobileNo1,'') AS MobileNo1,
                IFNULL(C.Address,'') AS Address,
                IFNULL(DATE_FORMAT(C.CreatedDate,"%d-%m-%Y %H:%i:%s"),'') AS CreatedDate,
                IFNULL(C.Status,'') AS Status,
                IFNULL(CONCAT(AD.FirstName," ",AD.LastName),' ') AS EmployeeName
              FROM sssm_customer C
              INNER JOIN sssm_admindetails AD  ON AD.UserID=C.EmployeeID
              LEFT JOIN sssm_customerproperty CP ON (CP.CustomerID = C.CustomerID)
              LEFT JOIN sssm_property PR ON(PR.PropertyID = CP.PropertyID)
              WHERE 
                CONCAT(C.FirstName,' ',C.LastName) LIKE CONCAT("%",IFNULL(_Name,IFNULL(C.FirstName,C.LastName)),"%") AND
                C.EmailID LIKE CONCAT("%",_EmailID,"%") AND
                ( C.MobileNo LIKE CONCAT("%",IFNULL(_MobileNo,C.MobileNo),"%") OR
                C.MobileNo1 LIKE CONCAT("%",IFNULL(_MobileNo,C.MobileNo1),"%") )AND
                C.Status = IFNULL(_Status,C.Status) AND 
                ( 
                  FIND_IN_SET(PR.ProjectID,@Projects) OR  PR.ProjectID IS NULL
                )AND
                IFNULL(CP.PropertyID,0) = IFNULL(_PropertyID,IFNULL(CP.PropertyID,0))
                GROUP BY C.CustomerID
        ) V ORDER BY V.CustomerID DESC
      ) AA
      WHERE Rno BETWEEN (((_CurrentPage*_PageSize) - _PageSize)+1) AND (_CurrentPage*_PageSize);
    END IF;
  ELSE 
    SELECT Fn_M_getErrorMessage('customer_listing_not_found') AS Message;
  END IF;
END$$

