CREATE DEFINER=`` PROCEDURE `usp_A_GetCustomerProcess` (IN `_PageSize` INT, IN `_CurrentPage` INT, IN `_ID` INT, IN `_Type` ENUM('Customer','Visitor','CustomerProperty'))  NO SQL BEGIN
IF(_Type = "CustomerProperty") THEN
    
    SET @CustomerID = (SELECT CustomerID FROM sssm_customerproperty WHERE CustomerPropertyID = _ID);
    SET @VistorID = (SELECT IFNULL(VisitorID,0) FROM sssm_customer WHERE CustomerID = @CustomerID);
    
    SET @CustomerPropertyDocument = (SELECT GROUP_CONCAT(CustomerPropertyDocumentID) FROM sssm_customerpropertydocument WHERE CustomerPropertyID = _ID);
    SET @CustomerPayment = (SELECT GROUP_CONCAT(CustomerReminderID) FROM sssm_customerreminder WHERE CustomerPropertyID = _ID);
    SET @CustomerPayment = (SELECT GROUP_CONCAT(CustomerPaymentID) FROM sssm_customerpayment WHERE CustomerPropertyID = _ID);
    
    SET @COUNT = (
            SELECT IFNULL(COUNT(P.CustomerProcessID),0) AS CCount FROM sssm_customerprocess P
            WHERE ((P.CustomerType = "Customer" AND P.ID = @CustomerID)
                        OR (P.ID = @VistorID AND P.CustomerType = "Visitor"))
            
            AND IF((ActionType='AddCustomer' OR ActionType = 'EditCustomer'), P.ActionID, 1) = IF((ActionType='AddCustomer' OR ActionType = 'EditCustomer'), @CustomerID, 1)
            AND IF((ActionType='AddCustomerProperty' OR ActionType = 'EditCustomerProperty'), P.ActionID, 1) = IF((ActionType='AddCustomerProperty' OR ActionType = 'EditCustomerProperty'), _ID, 1)
            AND FIND_IN_SET(IF((ActionType='AddCustomerDocument' OR ActionType = 'EditCustomerDocument'), P.ActionID,1), IF((ActionType='AddCustomerDocument' OR ActionType = 'EditCustomerDocument'), @CustomerPropertyDocument, 1)) 
            AND FIND_IN_SET(IF((ActionType='AddCustomerPayment' OR ActionType = 'EditCustomerPayment'), P.ActionID,1), IF((ActionType='AddCustomerPayment' OR ActionType = 'EditCustomerPayment'), @CustomerPayment, 1)) 
            AND FIND_IN_SET(IF((ActionType='CustomerReminder' OR ActionType = 'EditCustomerReminder'), P.ActionID,1), IF((ActionType='CustomerReminder' OR ActionType = 'EditCustomerReminder'), @CustomerPayment, 1)) 
        );
    IF(@COUNT > 0) THEN
        IF(_PageSize = -1) THEN
            SELECT A.* FROM (
                SELECT 
                    IFNULL(P.CustomerProcessID,'0') AS CustomerProcessID,
                    IFNULL(P.ID,'0') AS ID,
                    IFNULL(P.CustomerType,'') AS CustomerType,
                    IFNULL(P.Discription,'') AS Discription,
                    IFNULL(P.ActionID,'0') AS ActionID,
                    IFNULL(P.ActionType,'') AS ActionType,
                    IFNULL(P.CreatedBy,'0') AS CreatedBy,
                    IFNULL(P.CreatedDate,'') AS CreatedDate,
                    IFNULL(Fn_A_ChangeDateTimeFormat(P.CreatedDate),'') AS ProcessDate,
                    (SELECT IFNULL(FirstName,'') FROM sssm_admindetails WHERE UserID = P.CreatedBy) AS EmployeeFirstName,
                    (SELECT IFNULL(LastName,'') FROM sssm_admindetails WHERE UserID = P.CreatedBy) AS EmployeeLastName
                FROM sssm_customerprocess P
            WHERE  ((P.CustomerType = "Customer" AND P.ID = @CustomerID)
                        OR (P.ID = @VistorID AND P.CustomerType = "Visitor"))
            
            AND IF((ActionType='AddCustomer' OR ActionType = 'EditCustomer'), P.ActionID, 1) = IF((ActionType='AddCustomer' OR ActionType = 'EditCustomer'), @CustomerID, 1)
            AND IF((ActionType='AddCustomerProperty' OR ActionType = 'EditCustomerProperty'), P.ActionID, 1) = IF((ActionType='AddCustomerProperty' OR ActionType = 'EditCustomerProperty'), _ID, 1)
            AND FIND_IN_SET(IF((ActionType='AddCustomerDocument' OR ActionType = 'EditCustomerDocument'), P.ActionID,1), IF((ActionType='AddCustomerDocument' OR ActionType = 'EditCustomerDocument'), @CustomerPropertyDocument, 1)) 
            AND FIND_IN_SET(IF((ActionType='AddCustomerPayment' OR ActionType = 'EditCustomerPayment'), P.ActionID,1), IF((ActionType='AddCustomerPayment' OR ActionType = 'EditCustomerPayment'), @CustomerPayment, 1)) 
            AND FIND_IN_SET(IF((ActionType='CustomerReminder' OR ActionType = 'EditCustomerReminder'), P.ActionID,1), IF((ActionType='CustomerReminder' OR ActionType = 'EditCustomerReminder'), @CustomerPayment, 1)) 
            ) A ORDER BY A.CreatedDate Desc,A.CustomerProcessID Desc;
        ELSE
            SET @rownum:=0;
                SELECT AA.*,@rownum AS rowcount FROM (
                    SELECT V.*, @rownum := @rownum + 1 AS Rno FROM ( 
                        SELECT 
                            IFNULL(P.CustomerProcessID,'0') AS CustomerProcessID,
                            IFNULL(P.ID,'0') AS ID,
                            IFNULL(P.CustomerType,'') AS CustomerType,
                            IFNULL(P.Discription,'') AS Discription,
                            IFNULL(P.ActionID,'0') AS ActionID,
                            IFNULL(P.ActionType,'') AS ActionType,
                            IFNULL(P.CreatedBy,'0') AS CreatedBy,
                            IFNULL(P.CreatedDate,'') AS CreatedDate,
                            IFNULL(Fn_A_ChangeDateTimeFormat(P.CreatedDate),'') AS ProcessDate,
                            (SELECT IFNULL(FirstName,'') FROM sssm_admindetails WHERE UserID = P.CreatedBy) AS EmployeeFirstName,
                            (SELECT IFNULL(LastName,'') FROM sssm_admindetails WHERE UserID = P.CreatedBy) AS EmployeeLastName
                        FROM sssm_customerprocess P
            WHERE  ((P.CustomerType = "Customer" AND P.ID = @CustomerID)
                        OR (P.ID = @VistorID AND P.CustomerType = "Visitor"))
            
            AND IF((ActionType='AddCustomer' OR ActionType = 'EditCustomer'), P.ActionID, 1) = IF((ActionType='AddCustomer' OR ActionType = 'EditCustomer'), @CustomerID, 1)
            AND IF((ActionType='AddCustomerProperty' OR ActionType = 'EditCustomerProperty'), P.ActionID, 1) = IF((ActionType='AddCustomerProperty' OR ActionType = 'EditCustomerProperty'), _ID, 1)
            AND FIND_IN_SET(IF((ActionType='AddCustomerDocument' OR ActionType = 'EditCustomerDocument'), P.ActionID,1), IF((ActionType='AddCustomerDocument' OR ActionType = 'EditCustomerDocument'), @CustomerPropertyDocument, 1)) 
            AND FIND_IN_SET(IF((ActionType='AddCustomerPayment' OR ActionType = 'EditCustomerPayment'), P.ActionID,1), IF((ActionType='AddCustomerPayment' OR ActionType = 'EditCustomerPayment'), @CustomerPayment, 1)) 
            AND FIND_IN_SET(IF((ActionType='CustomerReminder' OR ActionType = 'EditCustomerReminder'), P.ActionID,1), IF((ActionType='CustomerReminder' OR ActionType = 'EditCustomerReminder'), @CustomerPayment, 1)) 
                    ) V ORDER BY V.CreatedDate Desc,V.CustomerProcessID Desc
                ) AA
                WHERE Rno BETWEEN (((_CurrentPage*_PageSize) - _PageSize)+1) AND (_CurrentPage*_PageSize);
        END IF;
    ELSE 
        SELECT Fn_M_getErrorMessage('process_not_found') AS Message;
    END IF;
ELSE
    
        SELECT Fn_M_getErrorMessage('type_not_define') AS Message;
    
END IF;
END$$

