                  IFNULL(P.Title,'') AS Title,
                  IFNULL(CR.Amount,0) AS Amount, 
                  IFNULL(CR.Message,'') AS Message, 
                  Fn_A_ChangeDateTimeFormat(CR.ReminderDate) AS ReminderDate,
                  IFNULL(CR.CreatedDate,0) AS CreatedDate,
                  IFNULL(CR.Status,0) AS Status,
                  '3' AS ReminderOrder 
                FROM sssm_customerreminder AS CR 
                  INNER JOIN sssm_customerproperty CP ON (CP.CustomerPropertyID = CR.CustomerPropertyID)
                  INNER JOIN sssm_customer CU ON (CU.CustomerID = CP.CustomerID)
                  INNER JOIN sssm_property PT ON (PT.PropertyID = CP.PropertyID)
                  INNER JOIN sssm_project P ON (P.ProjectID = PT.ProjectID)
                WHERE 
                    CP.CustomerPropertyID = IFNULL(_ID, CP.CustomerPropertyID) AND
                    P.ProjectID = IFNULL(_ProjectID, P.ProjectID) AND
                DATE_FORMAT(CR.ReminderDate,'%Y-%m-%d') < DATE_FORMAT(CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone),'%Y-%m-%d') AND 
                FIND_IN_SET(P.ProjectID,IFNULL(@Projects,-1))
                ORDER BY CR.ReminderDate DESC)
          ) V  ORDER BY V.ReminderDate DESC
      ) AA
      WHERE Rno BETWEEN (((_CurrentPage*_PageSize) - _PageSize)+1) AND (_CurrentPage*_PageSize);
