CREATE DEFINER=`` PROCEDURE `usp_A_GetReportPayment` (IN `_PageSize` INT, IN `_CurrentPage` INT, IN `_RoleID` INT, IN `_ProjectID` INT, IN `_PropertyID` INT, IN `_PaymentFromDate` VARCHAR(20), IN `_PaymentToDate` VARCHAR(20), IN `_DType` VARCHAR(20))  NO SQL BEGIN

IF(_ProjectID = -1) THEN SET _ProjectID =NULL; END IF;
IF(_PropertyID = -1) THEN SET _PropertyID =NULL; END IF;
SET @TMPRoleID  = _RoleID;
IF(_RoleID = -1 || _RoleID = -2) THEN SET _RoleID = NULL; END IF;
IF(_PaymentFromDate = '') THEN SET _PaymentFromDate =NULL; END IF; 
IF(_PaymentToDate = '') THEN SET _PaymentToDate =NULL; END IF; 
IF(_DType = "Web")THEN
  SET @ModuleID = 20;
ELSE
  SET @ModuleID = 38;
END IF;
SET @Projects = _ProjectID;
IF(_RoleID IS NOT NULL)THEN 
    IF(_ProjectID IS NULL) THEN 
      SET @Projects = (SELECT GROUP_CONCAT(P.ProjectID) FROM sssm_project P 
                      INNER JOIN sssm_roleproject RP ON (RP.ProjectID = P.ProjectID)
                      INNER JOIN sssm_rolemap RM ON (RM.RoleProjectID = RP.RoleProjectID AND RM.is_view = 1 AND RM.ModuleID = @ModuleID)
                      WHERE 
                        RP.RoleID = _RoleID AND P.Status = 1);
    ELSE
      SET @RoleProjectID = (SELECT RoleProjectID FROM sssm_roleproject WHERE RoleID = _RoleID AND ProjectID = _ProjectID);
      SET @Flag = (SELECT IFNULL(is_view,0) FROM sssm_rolemap WHERE RoleProjectID = @RoleProjectID AND ModuleID = @ModuleID);
      IF(IFNULL(@Flag,0) = 0) THEN
        SET @Projects = NULL;
      END IF;
    END IF;
ELSE 
    IF(_ProjectID IS NULL) THEN 
      SET @Projects = (SELECT GROUP_CONCAT(ProjectID) FROM sssm_project WHERE Status = 1);
    END IF;
END IF;
SET @cnt = (SELECT COUNT(CP.CustomerPaymentID) FROM sssm_customerpayment CP
            INNER JOIN sssm_customerproperty CPR ON (CPR.CustomerPropertyID = CP.CustomerPropertyID)
            INNER JOIN sssm_property PT ON (PT.PropertyID = CPR.PropertyID)
            INNER JOIN sssm_project P ON (P.ProjectID = PT.ProjectID)
            WHERE 
                FIND_IN_SET(PT.ProjectID,@Projects) AND 
                CPR.PropertyID = IFNULL(_PropertyID,CPR.PropertyID)
);

IF(@cnt > 0) THEN 
IF(_PageSize = -1) THEN
    SELECT 
