CREATE DEFINER=`` PROCEDURE `usp_GetAnniversaryUpcoming` (IN `_PageSize` INT, IN `_CurrentPage` INT, IN `_AnniversaryDate` INT, IN `_AnniversaryMonth` INT)  NO SQL BEGIN
IF(_AnniversaryDate = -1) THEN SET _AnniversaryDate = NULL; END IF;
IF(_AnniversaryMonth = -1) THEN SET _AnniversaryMonth = NULL; END IF;

SET @Count = (
              SELECT 
                COUNT(V.VisitorID) 
              FROM sssm_visitor V
              INNER JOIN sssm_admindetails A ON A.UserID = V.EmployeeID
              LEFT JOIN sssm_designation D ON D.DesignationID = V.DesignationID
              WHERE 
                V.AnniversaryDate = IFNULL(_AnniversaryDate,V.AnniversaryDate) AND
                V.AnniversaryMonth = IFNULL(_AnniversaryMonth,V.BirthMonth)
              );
IF(_PageSize = -1 ) THEN 
  SET _PageSize = @Count*10; 
  SET _CurrentPage = 1; 
END IF; 
IF(@Count > 0) THEN
  SET @rownum:=0;
  SELECT AA.*,@rownum AS rowcount FROM (
    SELECT V.*, @rownum := @rownum + 1 AS Rno FROM ( 
      SELECT 
        IFNULL(V.VisitorID,0) AS VisitorID, 
        IFNULL(V.EmployeeID,0) AS EmployeeID,
        IFNULL(A.FirstName,'') AS EmployeeFirstName,
        IFNULL(A.LastName,'') AS EmployeeLastName,
        CONCAT(IFNULL(A.FirstName,''),' ',IFNULL(A.LastName,'')) AS EmployeeName,
        IFNULL(V.FirstName,'') AS FirstName,
        IFNULL(V.LastName,'') AS LastName, 
        IFNULL(V.EmailID,'') AS EmailID,
        IFNULL(V.MobileNo,'') AS MobileNo,
        IFNULL(V.Status,0) AS Status
      FROM sssm_visitor V
      INNER JOIN sssm_admindetails A ON A.UserID = V.EmployeeID
      LEFT JOIN sssm_designation D ON D.DesignationID = V.DesignationID
      WHERE 
        V.AnniversaryDate = IFNULL(_AnniversaryDate,V.AnniversaryDate) AND
        V.AnniversaryMonth = IFNULL(_AnniversaryMonth,V.BirthMonth)
    ) V  
  ) AA WHERE Rno BETWEEN (((_CurrentPage*_PageSize) - _PageSize)+1) AND (_CurrentPage*_PageSize);
