                      IFNULL(PJ.ProjectID,'') AS ProjectID,
                      IFNULL(PJ.Location,'') AS Location,
                      IFNULL(CP.IsCancelled,0) AS IsCancelled,
                      (Fn_GetRoleProjectByModule(@TMPRoleID,PJ.ProjectID,@ModuleID,'is_price')) AS is_price,
                      (Fn_GetRoleProjectByModule(@TMPRoleID,PJ.ProjectID,@ModuleID,'is_edit')) AS is_edit,
                      (Fn_GetRoleProjectByModule(@TMPRoleID,PJ.ProjectID,@ModuleID,'is_status')) AS is_status,
                      (Fn_TotremPayByCustomer(CP.CustomerPropertyID)) AS RemainingPayment,
                      (Fn_TotnoOfPayByCustomer(CP.CustomerPropertyID)) AS TotalPayment,
                      (Fn_TotnoOfPayment(CP.CustomerPropertyID)) AS TotalNoOfPayment,
                      (Fn_GetGSTAmount(CP.CustomerPropertyID,1)) AS RemainingGSTPayment,
                      (Fn_GetGSTAmount(CP.CustomerPropertyID,0)) AS TotalGSTPayment,
                      IFNULL(PJ.ATSPercentage,0) AS ATSPercentage,
                      IFNULL(CP.ChannelPartner,'') AS ChannelPartner, 
                      IFNULL(CP.IsVerified,'') AS IsVerified, 
                      IFNULL(CP.IsATS,'') AS IsATS, 
                      IFNULL(CP.IsSD,'') AS IsSD,
                      IFNULL(CP.IsHold,'') AS IsHold
                  FROM 
                      sssm_customerproperty CP
                  INNER JOIN sssm_property PT ON(PT.PropertyID = CP.PropertyID)
                  INNER JOIN sssm_project PJ ON(PJ.ProjectID = PT.ProjectID)
                  INNER JOIN sssm_customer C ON(C.CustomerID=CP.CustomerID)
                  INNER JOIN sssm_admindetails AD ON (AD.UserID=C.EmployeeID)
                  WHERE 
                      @StartDate <= DATE_FORMAT(CP.PurchaseDate,"%Y-%m-%d") AND 
                      @EndDate >= DATE_FORMAT(CP.PurchaseDate,"%Y-%m-%d") AND
                      FIND_IN_SET(PJ.ProjectID,@Projects) AND
                      CP.IsCancelled = 0 AND CP.Status=1
                  ORDER BY CP.PurchaseDate DESC
                ) V  
              ) AA
              WHERE Rno BETWEEN (((_CurrentPage*_PageSize) - _PageSize)+1) AND (_CurrentPage*_PageSize);
            END IF;
        ELSE 
            SELECT Fn_M_getErrorMessage('customerproperty_listing_not_found') AS Message;
        END IF;
    ELSE
        SELECT Fn_M_getErrorMessage('customerproperty_listing_not_found111') AS Message;
    END IF; 
END IF;
END$$

CREATE DEFINER=`` PROCEDURE `usp_MA_Dashboard_301018` (IN `_RoleID` INT, IN `_ProjectID` INT, IN `_DType` VARCHAR(50), IN `_FilterType` VARCHAR(50))  NO SQL BEGIN 
