SELECT @LAST_ID AS ID, Fn_A_AddCustomerProcess(@CustomerID,'Customer', CONCAT(@adminname , ' has add reminder of ', @customername,'(customer).'), _CreatedBy, @LAST_ID, 'CustomerReminder') as addProcess;

    
 COMMIT;
