		SET @customername = (SELECT CONCAT(C.FirstName,' ',C.LastName) FROM sssm_customer C WHERE C.CustomerID=@customerID);
        SET @Type = 'Customer';
        SET @Text = 'customer';
    ELSEIF(@ActionUser = "CustomerProperty") THEN 
		SET @customerID = (SELECT C.CustomerID FROM sssm_customerproperty C WHERE C.CustomerPropertyID=(SELECT UserReminderID FROM sssm_reminderaction WHERE ReminderActionID = _ID));
        
		SET @customername = (SELECT CONCAT(C.FirstName,' ',C.LastName) FROM sssm_customer C WHERE C.CustomerID=@customerID);
        SET @Type = 'Customer';
        SET @Text = 'customer';
    ELSEIF(@ActionUser = "CustomerReminder") THEN 
		SET @customerID = (SELECT UserReminderID FROM sssm_reminderaction WHERE ReminderActionID = _ID);
        
		SET @customername = (SELECT CONCAT(C.FirstName,' ',C.LastName) FROM sssm_customer C WHERE C.CustomerID=@customerID);
        SET @Type = 'Customer';
        SET @Text = 'customer';
    ELSEIF(@ActionUser = "Visitor") THEN 
		SET @customerID = (SELECT UserReminderID FROM sssm_reminderaction WHERE ReminderActionID = _ID);
