    SELECT @LAST_ID AS ID, Fn_A_AddCustomerProcess(@customerID,@Type, CONCAT(@adminname , ' has add reminder response to ',@Text,' (', @customername,').'), _ResponseBy, @LAST_ID, CONCAT(@ActionUser,'Action'))
    as addProcess;
COMMIT;
END$$

CREATE DEFINER=`` PROCEDURE `usp_M_ChangePassword` (IN `_UserID` INT, IN `OldPassword` VARCHAR(250), IN `NewPassword` VARCHAR(250))  NO SQL BEGIN
  DECLARE row_count INT DEFAULT 0;
  SET @res = (SELECT  COUNT(A.UserID) FROM sssm_admindetails A WHERE A.UserID = _UserID);
  IF(@res > 0) THEN 


SET @Resultdelete = (SELECT  COUNT(A.UserID) FROM sssm_admindetails A WHERE A.UserID = _UserID AND A.IsDeleted != 1 );
IF(@Resultdelete > 0) THEN

	  SET @res = (SELECT  COUNT(A.UserID) FROM sssm_admindetails A WHERE A.UserID = _UserID AND IsAllow=0);
	  IF(@res = 0) THEN 

		  SET @opassword = (SELECT A.Password FROM sssm_admindetails A WHERE A.UserID = _UserID);
		  IF(@opassword = OldPassword) THEN 
		    IF(OldPassword != NewPassword) THEN
		      UPDATE sssm_admindetails A SET A.Password = NewPassword WHERE A.UserID = _UserID;
		      SET row_count   = (SELECT ROW_COUNT());
		      IF(row_count > 0) THEN
		        SELECT Fn_M_getErrorMessage('password_updated') AS Message;
		      ELSE
		        SELECT Fn_M_getErrorMessage('password_not_updated') AS Message;
		      END IF;
		    ELSE 
		      SELECT Fn_M_getErrorMessage('old_password_new_password_must_different') AS Message; 
		    END IF;
		  ELSE 
		    SELECT Fn_M_getErrorMessage('old_password_does_not_match') AS Message;
		  END IF;
	  ELSE 
	    SELECT Fn_M_getErrorMessage('access_denied_contact_to_admin') AS Message;
	  END IF;
      
  ELSE 
    SELECT Fn_M_getErrorMessage('user_deleted_contact_to_admin') AS Message;
  END IF;
  
  ELSE 
  SELECT Fn_M_getErrorMessage('user_not_found') AS Message;
END IF;
END$$

CREATE DEFINER=`` PROCEDURE `usp_M_ChangePropertyStatus` (IN `_Type` VARCHAR(20), IN `_CustomerPropertyID` INT, IN `_UserID` INT)  NO SQL BEGIN

SET @EmployeeName = (SELECT CONCAT(A.FirstName,' ',A.LastName) FROM sssm_admindetails A WHERE A.UserID=_UserID LIMIT 1);

SET @CustomerID = (SELECT CP.CustomerID FROM sssm_customerproperty CP WHERE CP.CustomerPropertyID = _CustomerPropertyID LIMIT 1);

SET @Propertydetail = (SELECT CONCAT(PJ.Title,'(',PR.PropertyNo,')') FROM sssm_property PR INNER JOIN sssm_customerproperty CP ON (CP.PropertyID = PR.PropertyID) INNER JOIN sssm_project PJ ON (PJ.ProjectID=PR.ProjectID) WHERE CP.CustomerPropertyID = _CustomerPropertyID LIMIT 1);

IF(_Type="Verified")THEN
	SET  @TimeZone = (SELECT TimeZone  FROM sssm_config Limit 1);
	UPDATE sssm_customerproperty
		SET 
			IsVerified = 1,
            ModifiedBy = IFNULL(_UserID,0),
        	ModifiedDate = CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone)
		WHERE 
			CustomerPropertyID = _CustomerPropertyID;
            
	SELECT _CustomerPropertyID AS ID, 
    '1' AS IsVerified, 
    Fn_A_AddCustomerProcess(@CustomerID,'Customer', CONCAT(@EmployeeName , ' has verified property ', @Propertydetail,'.'), _UserID, _CustomerPropertyID, 'ChangePropertyStatus') as addProcess,
    @EmployeeName AS EmployeeName,
    @Propertydetail AS Propertydetail,
    CONCAT(@EmployeeName , ' has verified property ', @Propertydetail,'.') AS msg,
                IFNULL((SELECT A.FirstName FROM sssm_admindetails A WHERE A.UserID=_UserID),'') AS EmployeeFirstName,
                IFNULL((SELECT A.LastName FROM sssm_admindetails A WHERE A.UserID=_UserID),'') AS EmployeeLastName,
                IFNULL(C.CustomerID,0) AS CustomerID, 
                IFNULL(C.FirstName,'') AS FirstName,
                IFNULL(C.LastName,'') AS LastName,
                IFNULL(C.EmailID,'') AS EmailID,
                IFNULL(C.MobileNo,'') AS MobileNo,
                IFNULL(C.MobileNo1,'') AS MobileNo1,
                IFNULL(C.Address,'') AS Address,
                IFNULL(C.CreatedDate,'') AS CreatedDate,
                IFNULL(C.Status,'') AS Status,
                
                                
              (Fn_TotremPayByCustomer(@LAST_ID)) AS RemainingPayment,
              (Fn_TotnoOfPayByCustomer(@LAST_ID)) AS TotalPayment,
              (Fn_TotnoOfPayment(@LAST_ID)) AS TotalNoOfPayment,
                            @LAST_ID AS  CustomerPropertyID
             
            FROM sssm_customer C
            WHERE C.CustomerID = @CustomerID;
      
ELSEIF(_Type="ATS")THEN
	SET @TAmount = (SELECT Amount FROM sssm_customerproperty WHERE CustomerPropertyID = _CustomerPropertyID);
	SET @TotPayment = (SELECT Fn_TotnoOfPayByCustomer(_CustomerPropertyID));
	SET @Percentage = (SELECT PJ.ATSPercentage FROM sssm_customerproperty CP
				INNER JOIN sssm_property P ON (P.PropertyID = CP.PropertyID)
				INNER JOIN sssm_project PJ ON (PJ.ProjectID = P.ProjectID)
				WHERE 
					CP.CustomerPropertyID = _CustomerPropertyID);
	SET @PerAmount = (@TAmount * @Percentage) / 100;
	IF(@PerAmount <= @TotPayment) THEN
    	SET  @TimeZone = (SELECT TimeZone  FROM sssm_config Limit 1);
		UPDATE sssm_customerproperty
			SET IsATS = 1,
           ModifiedBy = IFNULL(_UserID,0),
        ModifiedDate = CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone)
			WHERE 
			CustomerPropertyID = _CustomerPropertyID;
		SELECT _CustomerPropertyID AS ID, 
        '1' AS IsATS, 
        Fn_A_AddCustomerProcess(@CustomerID,'Customer', CONCAT(@EmployeeName , ' has changed status of ATS for property ', @Propertydetail,'.'), _UserID, _CustomerPropertyID, 'ChangePropertyStatus') as addProcess,
    @EmployeeName AS EmployeeName,
    @Propertydetail AS Propertydetail,
    CONCAT(@EmployeeName , ' has changed status of ATS for property ', @Propertydetail,'.') AS msg,
