CREATE DEFINER=`` PROCEDURE `usp_M_ConvertVisitorToCustomer` (IN `_VisitorID` INT, IN `_UserID` INT, IN `_UserType` ENUM('Admin Web','Admin Android','Admin IOS','Employee Web','Employee Android','Employee IOS'), IN `_IPAddress` VARCHAR(15))  NO SQL BEGIN
    DECLARE EXIT handler for sqlexception
    BEGIN
        ROLLBACK;
        GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, @errno = MYSQL_ERRNO, @text = MESSAGE_TEXT; 
        SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
        SELECT "Convert Visitor To Customer" AS Method , @full_error AS Message;
    END;
    START TRANSACTION;

        SET  @TimeZone = (SELECT TimeZone  FROM sssm_config Limit 1);
        IF(IFNULL(@TimeZone,'') = "") THEN SET @TimeZone = "+00:00"; END IF;
        SET @isConverted = (SELECT COUNT(CustomerID) FROM sssm_customer WHERE VisitorID = _VisitorID);
        IF(@isConverted > 0 )THEN
          SELECT Fn_M_getErrorMessage('visitor_aleady_converted') as Message;
        ELSE
            SET @MobileNo = (SELECT MobileNo FROM sssm_visitor WHERE VisitorID = _VisitorID);
            SET @Email = (SELECT EmailID FROM sssm_visitor WHERE VisitorID = _VisitorID);
            SET @ECount = (SELECT COUNT(CustomerID) FROM sssm_customer WHERE EmailID = IF(@Email='','-1',@Email));
            SET @MCount = (SELECT COUNT(CustomerID) FROM sssm_customer WHERE MobileNo = @MobileNo);
            IF(@ECount > 0 OR @MCount > 0) THEN 
                IF(@ECount > 0) THEN
                    SELECT Fn_M_getErrorMessage('emailid_already_exist1') AS Message;
                ELSE
                    SELECT Fn_M_getErrorMessage('mobile_already_exist') AS Message;
                END IF;
            ELSE
                INSERT INTO sssm_customer(EmployeeID, 
                                            VisitorID, 
                                            FirstName, 
                                            LastName, 
                                            EmailID, 
                                            MobileNo, 
                                            MobileNo1, 
                                            Address, 
                                            CreatedBy, 
                                            CreatedDate,
                                            Status)
                                    SELECT EmployeeID, 
                                            VisitorID, 
                                            FirstName, 
                                            LastName, 
                                            EmailID, 
                                            MobileNo,
                                            '', 
                                            Address,
                                            IFNULL(_UserID,0),
                                            CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone),
                                            1 
                                            FROM sssm_visitor 
                                            WHERE VisitorID = _VisitorID;

                SET @LAST_ID = LAST_INSERT_ID();

                IF(@LAST_ID>0)THEN
        
                    
                    SET @adminname = (SELECT CONCAT(A.FirstName,' ',A.LastName) FROM sssm_admindetails A WHERE A.UserID=_UserID);

                    SET @CustomerID = @LAST_ID;

                    SET @customername = (SELECT CONCAT(C.FirstName,' ',C.LastName) FROM sssm_visitor C WHERE C.VisitorID=_VisitorID);

                    SET @process = (SELECT  Fn_A_AddCustomerProcess(@LAST_ID,'Customer', CONCAT(@adminname , ' has converted ', @customername,'(visitor) to customer.'), _UserID, @LAST_ID, 'AddCustomer') as addProcess);

        
                    UPDATE sssm_visitor 
                    SET 
                        Status = 0,
                        IsCustomer = 1,
                        IsIdle=1,
                        IdleDate =  CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone),
                        ModifiedBy = _UserID,
                        ModifiedDate =  CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone)
                    WHERE 
                        VisitorID = _VisitorID;

                    SET @visitorname = (SELECT CONCAT(FirstName,' ', LastName) FROM sssm_visitor WHERE VisitorID = _VisitorID LIMIT 1);
                    SET @ID=(SELECT Fn_A_AddActivityLog('ConvertVisitorToCustomer',CONCAT(' has Convert Visitor(',@visitorname,') to customer'),_UserType,_IPAddress,_UserID));
                                        
                    SELECT  @LAST_ID AS ID, Fn_M_getErrorMessage('ConvertVisitorToCustomer_successfully') as Message,
                      IFNULL(CustomerID,0) AS CustomerID, 
                      IFNULL(FirstName,'') AS FirstName,
                      IFNULL(LastName,'') AS LastName,
                      IFNULL(EmailID,'') AS EmailID,
                      IFNULL(MobileNo,'') AS MobileNo,
                      IFNULL(MobileNo1,'') AS MobileNo1,
                      IFNULL(Address,'') AS Address,
                      IFNULL(CreatedDate,'') AS CreatedDate,
                      IFNULL(Status,'') AS Status
                    FROM sssm_customer
                    WHERE CustomerID =  @LAST_ID;
                    
                ELSE
                  SELECT Fn_M_getErrorMessage('ConvertVisitorToCustomer_fail') as Message;
                END IF;                 END IF;                 END IF;                 COMMIT;
END$$

