SET @count = (SELECT COUNT(CT.CityID) FROM sssm_cities CT
                INNER JOIN sssm_state S ON (S.StateID = CT.StateID)
                INNER JOIN sssm_country C ON (S.CountryID = C.CountryID)
              WHERE C.Status = 1 AND S.Status = 1 AND CT.Status = 1 AND CT.StateID = IFNULL(_StateID,CT.StateID));
IF(@count > 0) THEN 
  SELECT 
    IFNULL(CT.CityID,0) AS ID,
    IFNULL(CT.StateID,0) AS StateID,
    IFNULL(CT.CityName,'') AS Name,
    IFNULL(CT.Status,0) AS Status
  FROM sssm_cities CT
    INNER JOIN sssm_state S ON (S.StateID = CT.StateID)
    INNER JOIN sssm_country C ON (S.CountryID = C.CountryID)
  WHERE 
      C.Status = 1 AND 
      S.Status = 1 AND 
      CT.Status = 1 AND 
      CT.StateID = IFNULL(_StateID,CT.StateID)
    ORDER BY CT.CityName;
ELSE 
  SELECT Fn_M_getErrorMessage('city_not_found') AS Message;
END IF;
END$$

