CREATE DEFINER=`` PROCEDURE `usp_M_GetCustomerDocumentByCProperty` (IN `_PageSize` INT, IN `_CurrentPage` INT, IN `_ID` INT, IN `_Path` VARCHAR(250))  NO SQL BEGIN
