CREATE DEFINER=`` PROCEDURE `usp_M_getDeviceByEmployee` (IN `_UserID` INT, IN `_Type` VARCHAR(50), IN `_ProjectID` INT)  NO SQL BEGIN
IF(_UserID = -1) THEN SET _UserID =NULL; END IF;
IF(_Type = -1) THEN SET _Type =NULL; END IF;
IF(_ProjectID = -1) THEN SET _ProjectID =NULL; END IF;

SET @RoleUser = (SELECT GROUP_CONCAT(A.UserID) FROM sssm_admindetails A 
                INNER JOIN sssm_roleproject RP ON RP.RoleID = A.RoleID
                INNER JOIN sssm_rolemap RM ON RM.RoleProjectID = RP.RoleProjectID
                INNER JOIN sssm_module M ON M.ModuleID = RM.ModuleID
                WHERE RP.ProjectID = IFNULL(_ProjectID,0)  AND 
                (M.Type = 'Mobile' OR M.Type = 'Both') AND
                RM.is_push = 1 AND
                    (IF(_Type='Customer','Customer','-1')=M.ModuleName
                    OR IF(_Type='Payment','Payment','-1')=M.ModuleName
                    OR IF(_Type='Document','Document','-1')=M.ModuleName
                    OR IF(_Type='CustomerReminder','Payment_Reminder','-1')=M.ModuleName
                    OR IF(_Type='CustomerProperty','Customer_Property','-1')=M.ModuleName
                    )
                );

SET @cnt = (SELECT COUNT(A.UserID) 
            FROM sssm_admindetails A 
            LEFT JOIN sssm_deviceinfo D ON (A.UserID = D.UserID AND D.Status = 1)
            INNER JOIN sssm_usersetting US ON US.UserID = A.UserID 
            WHERE (                  FIND_IN_SET(A.UserID, IFNULL(@RoleUser, '-1'))
                  OR A.IsAdmin=1 OR A.RoleID='-1' OR A.RoleID='-2') 
            
                AND A.Status = 1 
                AND A.IsDeleted!=1 
                AND US.IsPush=1 
            AND IF(_Type='Customer',US.Customer,1)=1
            AND IF(_Type='CustomerReminder',US.CustomerReminder,1)=1
            AND IF(_Type='CustomerProperty',US.CustomerProperty,1)=1
            AND IF(_Type='VisitorReminder',US.VisitorReminder,1)=1
            AND IF(_Type='Payment',US.Payment,1)=1
            AND IF(_Type='Document',US.Document,1)=1);
IF(@cnt > 0) THEN 

        SELECT 
         IFNULL(D.DeviceID,0) AS DeviceID,
         IFNULL(D.DeviceUID,0) AS DeviceUID,
         IFNULL(D.DeviceName,'') AS DeviceName,
         IFNULL(D.DeviceOS,'') AS DeviceOS,
         IFNULL(D.OSVersion,'') AS OSVersion,
         IFNULL(D.DeviceTokenID,'') AS DeviceTokenID,
         IFNULL(D.DeviceType,'') AS DeviceType,
         IFNULL(A.UserID,0) AS UserID,
         IFNULL(A.EmailID,0) AS EmailID,
         IFNULL(A.FirstName,0) AS FirstName,
         IFNULL(A.LastName,0) AS LastName,
         IFNULL(D.CreatedDate,'') AS CreatedDate,
         IFNULL(D.Status,0) AS Status
         
        FROM sssm_admindetails A 
        LEFT JOIN sssm_deviceinfo D ON (A.UserID = D.UserID AND D.Status = 1)
        INNER JOIN sssm_usersetting US ON US.UserID = A.UserID 
        WHERE (                FIND_IN_SET(A.UserID, IFNULL(@RoleUser, '-1'))
                OR A.IsAdmin=1 OR A.RoleID='-1' OR A.RoleID='-2') 
            
                AND A.Status = 1  
                AND A.IsDeleted!=1
                AND US.IsPush=1 
        AND IF(_Type='Customer',US.Customer,1)=1
        AND IF(_Type='CustomerReminder',US.CustomerReminder,1)=1
        AND IF(_Type='CustomerProperty',US.CustomerProperty,1)=1
        AND IF(_Type='VisitorReminder',US.VisitorReminder,1)=1
        AND IF(_Type='Payment',US.Payment,1)=1
        AND IF(_Type='Document',US.Document,1)=1
        ORDER BY CreatedDate DESC;

ELSE 
    SELECT Fn_M_getErrorMessage('deviceinfo_not_found') AS Message;
END IF;

END$$

