CREATE DEFINER=`` PROCEDURE `usp_M_GetNotification` (IN `_PageSize` INT, IN `_CurrentPage` INT, IN `_ID` INT, IN `_Path` VARCHAR(250))  NO SQL BEGIN
SET @cnt = (SELECT COUNT(NotificationID) FROM sssm_notification WHERE UserID = _ID);

IF(@cnt > 0) THEN 
IF(_PageSize = -1) THEN
SELECT 
  IFNULL(NotificationID,0) AS NotificationID,
  IFNULL(UserID,0) AS UserID,
  IFNULL(Description,'') AS Description,
  IFNULL(TypeID,0) AS TypeID,
  IFNULL(ActionType,'') AS ActionType,
  IFNULL(IsRead,0) AS IsRead,
  IFNULL(CreatedBy,0) AS CreatedBy,
  Fn_GetNotificationTitle(ActionType) AS Title,
  IFNULL(DATE_FORMAT(CreatedDate,'%d-%m-%Y %H:%i'),0) AS CreatedDate,
  CreatedDate AS CreatedDateOrd,
  Fn_M_GetNotificationRelatedData(TypeID,ActionType,_Path) AS Detail
FROM 
  sssm_notification 
WHERE 
  UserID = _ID
  ORDER BY CreatedDateOrd DESC ;
ELSE
   SET @rownum:=0;

 SELECT AA.*,@rownum AS rowcount FROM (
        SELECT V.*, @rownum := @rownum + 1 AS Rno
        FROM ( 
              SELECT 
                IFNULL(NotificationID,0) AS NotificationID,
                IFNULL(UserID,0) AS UserID,
                IFNULL(Description,'') AS Description,
                IFNULL(TypeID,0) AS TypeID,
                IFNULL(ActionType,'') AS ActionType,
                IFNULL(IsRead,0) AS IsRead,
                IFNULL(CreatedBy,0) AS CreatedBy,
            	Fn_GetNotificationTitle(ActionType) AS Title,
                IFNULL(DATE_FORMAT(CreatedDate,'%d-%m-%Y %H:%i'),0) AS CreatedDate,
            	CreatedDate AS CreatedDateOrd,
                Fn_M_GetNotificationRelatedData(TypeID,ActionType,_Path) AS Detail
              FROM 
                sssm_notification 
              WHERE 
                UserID = _ID
                             ) V  ORDER BY V.CreatedDateOrd DESC 
        ) AA
        WHERE Rno BETWEEN (((_CurrentPage*_PageSize) - _PageSize)+1) AND (_CurrentPage*_PageSize);
END IF;
ELSE 
    SELECT Fn_M_getErrorMessage('notification_listing_not_found') AS Message;
END IF;
END$$

