				IFNULL(_Password,''),
				CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone),
				0,
				1
	    );

		SET ResultId = LAST_INSERT_ID();
                
		IF(ResultId > 0) THEN
        	SET @add_device = 0;
        	INSERT INTO sssm_usersetting (
        		UserID,
				IsPush,
				VisitorReminder,
				Customer,
				CustomerReminder,
				CustomerProperty,
				Payment,
				Document,
				CreatedBy,
				CreatedDate
				) 
				VALUES(
				ResultId,
				1,
				1,
				1,
				1,
				1,
				1,
				1,
				ResultId,
				CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone)	
				);
	        IF(_NotificationToken!='') THEN
	       		SET @add_device = (SELECT Fn_M_AddDevice(_DeviceName,_DeviceUID,_OSVersion,_NotificationToken,_DeviceType,ResultId) as a);
