                IFNULL(US.CustomerProperty,1) AS CustomerProperty,
                IFNULL(US.Payment,1) AS Payment,
                IFNULL(US.Document,1) AS Document,

		        _NotificationToken as NotificationToken
		      FROM sssm_admindetails U
            	LEFT JOIN sssm_usersetting US ON US.UserID = U.UserID
		      WHERE  U.UserID = ResultId AND U.Status!=0;

