CREATE DEFINER=`` FUNCTION `Fn_GetVisitorNextReminderStatus` (`_VisitorID` INT) RETURNS INT(11) NO SQL BEGIN
SET  @TimeZone = (SELECT TimeZone  FROM sssm_config Limit 1);
IF(IFNULL(@TimeZone,'') = "") THEN SET @TimeZone = "+00:00"; END IF;

SET @Count = (
				SELECT 
					COUNT(VR.VisitorID)
				FROM 
					sssm_visitorreminder VR
    			INNER JOIN sssm_visitor V ON V.VisitorID = VR.VisitorID
				WHERE 
    				VR.VisitorID = _VisitorID AND
    				V.IsIdle != 1 AND
					VR.ReminderDate <=  DATE_ADD(CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone), INTERVAL 2 DAY)
				 	AND 
					VR.ReminderDate >= CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone)
			);
IF(@Count > 0) THEN
	RETURN 1;
ELSE 
	RETURN 0;
END IF;
END$$

